action.script:
# 使用am start启动activity的说明 # 添加Extras # --ei 整数参数【extra integer】 # --es 字符串参数【extra string】 # --ez Boolean【extra boolean】
url="https://cn.bing.com" activity="$PACKAGE_NAME/com.projectkr.shell.ActionPageOnline" am start -a android.intent.action.MAIN -n "$activity" --es url "$url"
# 对应到Java中取出参数的过程 # Intent intent = getIntent(); # String webPage = intent.getStringExtra("url");
